			for (j = 0; j < n; ++j ){
				sum += grid[i][j];
			} // end for
			res = max (res, sum );
		} // end for

		for (j = 0; j < n; ++j){
			sum = 0;
			for (i = 0; i < n; ++i ){
				sum += grid[i][j];
			} // end for
			res = max (res, sum );
		} // end for

		for (k = -n + 1; k < n; ++k){
			sum = 0;
			for (i = 0; i < n; ++i ){
				for (j = 0; j < n; ++j ){
					if (i - j == k ){
						sum += grid[i][j];
					} // end if
				} // end for
			} // end for
			res = max (res, sum );
		} // end for

		for (k = 0; k <= 2*(n-1); ++k){
			sum = 0;
			for (i = 0; i < n; ++i){
