int solve(){
    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            v[i][j] = h[i][j] = rd[i][j] = ld[i][j] = 0;
        }
    }

    if(mat[0][0]=='1')  v[0][0] = h[0][0] = rd[0][0] = ld[0][0] = 1;

    for(int j = 1; j < n; j++){
        if(mat[0][j]=='1'){
            h[0][j] = h[0][j-1]+1;
