			for (int k = 6; k < 8; ++k) {
				int py = ny - ady[k];
				int px = nx - adx[k];
				if (!(px >= 0 && px <W && py >= 0 && py <H))continue;

				sums[ny][nx][k] = add(sums[py][px][k], field[ny][nx]);

			}
		}
	}
	return sums;
}

