struct UnionFind {
  vector<int> data;
  UnionFind(int size){
    rep(i in up(0,size)) data.pb(i); 
  }

  void unionSet(int x, int y) {
    x = root(x);
    y = root(y);
    if(x != y) data[y] = data[x];
  }

  bool findSet(int x, int y) {
    return root(x) == root(y);
  }

  int root(int x) {
    if(data[x] == x) return x;
    else root(data[x]);
  }

  int size() {
    return data.size();
  }
};

int ans,n;

void nnm(vector<string> vec,int x, int y, bool go_rigth,int cnt){
  if(x >= n - 1 || y >= n - 1) return;

  if(vec[y][x] == '1'){
    cnt++;
    ans = max(ans, cnt);
  }else{
    cnt = 0;
  }

  if(go_rigth) x++;
  else x--;

  y++;
  if(x <= 0 || y <= 0) return;
  nnm(vec,x, y, go_rigth, cnt);
}

int main(){
  int h_cnt,w_cnt;
  while(cin >> n, n){
    vector<string> l(n); 
    ans = 1;
    bool hw_found = false,found_1 = false;
    rep(i in up(0,n)){
      w_cnt = 0;
      cin >> l[i];
      if(!found_1 && count(all(l[i]),'1')){
        found_1 = true;
      }

      rep(j in up(0,n)){
        if(l[i][j] == '1'){
          w_cnt++;
        }else{
          w_cnt = 0; 
        }
        ans = max(w_cnt, ans);
      }

    }

    if(!found_1){
      cout << 0 << endl; 
      return 0;
    }

    if(!hw_found){
      rep(i in up(0,n)){
        h_cnt = 0;
        rep(j in up(0,n)){
          if(l[j][i] == '1') h_cnt++;
          else h_cnt = 0; 
          ans = max(h_cnt, ans);
        }
      }
    }

    rep(i in up(0,n)){
      rep(j in up(0,n)){
        if(l[i][j] == '1'){
          nnm(l,i,j,true,1);
          nnm(l,i,j,false,1);
        }
      }
    }

    cout << ans << endl;
  }
}

