      cin >> mass[i];

    int ans = 0;
    vector<int> sum(6, 0);
    for (int i = 0; i < n; ++i) {
      // hor, ver
      for (int j = 0; j < n; ++j) {
	sum[0] += mass[i][j] - '0';
	sum[1] += mass[j][i] - '0';
      }

