      for (int j = 0; i+j < n; ++j) {
	sum[2] += mass[i+j][j] - '0';
	sum[3] += mass[n-1-i-j][j] - '0';
	sum[4] += mass[n-1-i-j][n-1-j] - '0';
	sum[5] += mass[i+j][n-1-j] - '0';
      }

      for (unsigned int j = 0; j < sum.size(); ++j) {
	ans = max(ans, sum[j]);
	sum[j] = 0;
      }
    }

    cout << ans << endl;
  }
  return 0;
}

