      }
    }

    // right down
    for(int i=0; i<N; i++) {
      int sx = 0, sy = i;
      int cnt = 0;
      for(int k=0; k<N; k++) {
	if(!CK(sy+k, 0, N) || !CK(sx+k, 0, N)) break;
	if(!grid[sy+k][sx+k]) cnt = 0;
	else cnt ++;
	ans = max(ans, cnt);
      }
    }

    for(int i=0; i<N; i++) {
      int sx = i, sy = 0;
      int cnt = 0;
      for(int k=0; k<N; k++) {
	if(!CK(sy+k, 0, N) || !CK(sx-k, 0, N)) break;
	if(!grid[sy+k][sx-k]) cnt = 0;
	else cnt ++;
	ans = max(ans, cnt);
      }
    }

    cout << ans << endl;
  }

  return 0;
}

