bool line(vector<string> v, int len)
{
	int n = v.size();


	rep(i, len)
	{
		int x = len - 1 + (n - 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = (n - 1) * (n - len + 1) + (n - 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = n - len + (n + 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = n * (n - len) + (n + 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	return false;
}
