};

int search(int **a, int x, int y, int n, int k)
{
	if(x < 0 || y < 0 || x == n || y == n || a[x][y] == 0){
		return 0;
	} else {
		return (search(a, (x+d[k][0]), (y+d[k][1]), n, k) + 1);
	}
}

