int search(int x, int y, int k, int n, char** field) {
    int nx, ny;
    switch (k) {
    case 0:
        nx = x + 1;
        ny = y;
        break;
    case 1:
        nx = x + 1;
        ny = y + 1;
        break;
    case 2:
        nx = x;
        ny = y + 1;
        break;
    case 3:
        nx = x - 1;
        ny = y + 1;
        break;
    }
    if (0 <= nx && nx < n && 0 <= ny && ny < n && field[ny][nx] != '0') {
        return 1 + search(nx, ny, k, n, field);
    } else {
        return 1;
    }
}

