                if(j == l - 1) MAX = max(MAX, l);
            }
        }
        rep(i, 0, n){
            int l = n - i;
            rep(j, 0, l){
                if(d[j][i + j] == '0') break;
                if(j == l - 1) MAX = max(MAX, l);
            }
        }
