  while(cin >> n, n){
    vector<string> l(n); 
    ans = 1;
    bool hw_found = false,found_1 = false;

    rep(i in up(0,n)){
      cin >> l[i];
      if(!found_1 && count(all(l[i]),'1')){
        found_1 = true;
      }

      if(*(min_element(all(l[i]))) == '1' && !hw_found){
        ans = n;
        hw_found = true;
      }
    }

    if(!found_1){
      cout << 0 << endl; 
      return 0;
    }

    int h_cnt ;
    if(!hw_found){
      rep(i in up(0,n)){
        h_cnt = 0;
        rep(j in up(0,n)){
          if(l[j][i] == '1') h_cnt++;
          else h_cnt = 0; 
          ans = max(h_cnt, ans);
        }
      }
    }

    rep(i in up(0,n)){
      rep(j in up(0,n)){
        if(l[i][j] == '1'){
          nnm(l,i,j,true,1);
          nnm(l,i,j,false,1);
        }
      }
    }     

    cout << ans << endl;
  }
}

