int dfs(int y, int x, int dy, int dx, vector<vector<int>> const& v) {
    int n = v.size();
    if(y < 0 || n <= y || x < 0 || n <= x || v[y][x] == 0) {
        return 0;
    }
    return dfs(y+dy, x+dx, dy, dx, v) + 1;
}

