        if(g[x][y] == '1') s++;
        x -= 1, y += 1;
        if(x == -1 || y == n) break;
      }
      x = n - 1, y = i;
      while(1) {
        if(g[x][y] == '1') ss++;
        x -= 1, y -= 1;
        if(x == 0 || y == 0) break;
      }
      ma = max(ma, max(s, ss));
    }

    cout << ma << endl;
  }
}

