bool line(vector<string> v, int len)
{
	int n = v.size();
	int x = len - 1, y = 0;

	// ツ右ツ湘」ツつゥツづァツ湘」ツ板シツ閉ェ
	rep(i, len)
	{
		if(v[y][x] == '0') break;
		else if(i + 1 == len) return true;
		x--;
		y++;
	}

	// ツ右ツ湘」ツつゥツづァツ可コツ板シツ閉ェ
	x = n - 1;
	y = n - len;
	rep(i, len)
	{
		if(v[y][x] == '0') break;
		else if(i + 1 == len) return true;
		x--;
		y++;
	}

	// ツ債カツ湘」ツつゥツづァツ湘」ツ板シツ閉ェ
	x = n - len;
	y = 0;
	rep(i, len)
	{
		if(v[y][x] == '0') break;
		else if(i + 1 == len) return true;
		x++;
		y++;
	}

	// ツ債カツ湘」ツつゥツづァツ可コツ板シツ閉ェ
	x = 0;
	y = n - len;
	rep(i, len)
	{
		if(v[y][x] == '0') break;
		else if(i + 1 == len) return true;
		x++;
		y++;
	}

	return false;	
}
