	  for(int l=0; l<k; l++) {
	    if(!grid[i+l][j+l]) ok = 0;
	  }
	  if(ok) {
	    ans = max(ans, k);
	  }
	}
      }
    }

    // right up
    for(int i=0; i<N; i++) {
      for(int j=0; j<N; j++) {
	for(int k=1; k<=N; k++) {
	  if(i-k+1<0 || j+k>N) continue;

	  bool ok = 1;
	  for(int l=0; l<k; l++) {
	    if(!grid[i-l][j+l]) ok = 0;
	  }
	  if(ok) {
