struct UnionFind {
  vector<int> data;
  UnionFind(int size){
    rep(i in up(0,size)) data.pb(i); 
  }

  void unionSet(int x, int y) {
    x = root(x);
    y = root(y);
    if(x != y) data[y] = data[x];
  }

  bool findSet(int x, int y) {
    return root(x) == root(y);
  }

  int root(int x) {
    if(data[x] == x) return x;
    else root(data[x]);
  }

  int size() {
    return data.size();
  }
};

int ans,n;

void nnm(vector<string> vec,int x, int y, bool go_rigth,int cnt){
  if(x >= n - 1 || y >= n - 1) return;

  if(vec[y][x] == '1'){
    cnt++;
    ans = max(ans, cnt);
  }else{
    cnt = 0;
  }

  if(go_rigth) x++;
  else x--;

  y++;
  if(x <= 0 || y <= 0) return;
  nnm(vec,x, y, go_rigth, cnt);
}

int main(){
  int h_cnt,w_cnt;
  while(cin >> n, n){
    vector<string> l(n); 
    ans = 1;
    bool hw_found = false,found_1 = false;
    rep(i in up(0,n)){
      w_cnt = 0;
      cin >> l[i];
      if(!found_1 && count(all(l[i]),'1')){
        found_1 = true;
      }

      rep(j in up(0,n)){
        if(l[i][j] == '1'){
          w_cnt++;
        }else{
          w_cnt = 0; 
        }
        ans = max(w_cnt, ans);
      }
    }

    if(!found_1){
      cout << 0 << endl; 
      continue;
    }

    if(!hw_found){
      rep(i in up(0,n)){
        h_cnt = 0;
        rep(j in up(0,n)){
          if(l[j][i] == '1') h_cnt++;
          else h_cnt = 0; 
          ans = max(h_cnt, ans);
        }
      }
    }

    rep(i in up(0,n)){
      rep(j in up(0,n)){
        if(l[i][j] == '1'){
          nnm(l,i,j,true,1);
          nnm(l,i,j,false,1);
        }
      }
    }

    cout << ans << endl;
  }
}

#include<iostream>
using namespace std;

int n;
char mat[255][255];
// vertical
int v[255][255];
// horizontal
int h[255][255];
// right-down diagonally
int rd[255][255];
// left-down diagonally
int ld[255][255];

int solve(){
    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            v[i][j] = h[i][j] = rd[i][j] = ld[i][j] = 0;
        }
    }

    for(int j = 0; j < n; j++){
        if(mat[0][j]=='1')  v[0][j] = h[0][j] = rd[0][j] = ld[0][j] = 1;
    }

    for(int i = 1; i < n; i++){
        for(int j = 0; j < n; j++){
            if(mat[i][j]=='1'){
                if(j != 0)      h[i][j] = h[i][j-1]+1;    
                else            h[i][j] = 1;
                v[i][j] = v[i-1][j]+1;
                if(j != n-1)    ld[i][j] = ld[i-1][j+1]+1;
                else            ld[i][j] = 1;
                if(j != 0)      rd[i][j] = rd[i-1][j-1]+1;
                else            rd[i][j] = 1;
            }
        }
    }

    int ret = 0;
    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            ret = max(ret, max(max(max(h[i][j], v[i][j]), rd[i][j]),ld[i][j]));
        }
    }

    return ret;
}

int main(){
    while(cin >> n){
        if(n == 0)  break;

        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                cin >> mat[i][j];
            }
        }

        cout << solve() << endl;
    }

    return 0;
}

#include <bits/stdc++.h>

using namespace std;

const int MAX_N = 300;

int n, cnt;
int in[MAX_N][MAX_N];
int s[MAX_N][MAX_N];

void cp_in(){
    for(int i = 0 ; i < n ; i++){
        for(int j = 0 ; j < n ; j++){
            s[i][j] = in[i][j];
        }
    }
}

void dfs(int y, int x, bool bit)
{
    if (bit) {
        if (n <= x || n <= y) return;
        cnt++;
        int nx = x + 1;
        int ny = y + 1;

        if (0 <= nx && nx < n && 0 <= ny && ny < n && s[ny][nx] == 1) {
            dfs(ny, nx, bit);
        }
    } else {
        if (x < 0 || n <= y) return;
        cnt++;
        int nx = x - 1;
        int ny = y + 1;

        if (0 <= nx && nx < n && 0 <= ny && ny < n && s[ny][nx] == 1) {
            dfs(ny, nx, bit);
        }
    }
}

int main()
{
    char ch;
    while (cin >> n, n) {
        int a = 0, b = 0, c = 0, d = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                cin >> ch;
                in[i][j] = ch - '0';
            }
        }
        for (int i = 0; i < n; i++) {
            cnt = 0;
            for (int j = 0; j < n; j++) {
                if (in[i][j] == 1) {
                    cnt++;
                    a = max(a, cnt);
                } else {
                    cnt = 0;
                }
            }
        }

        for (int i = 0; i < n; i++) {
            cnt = 0;
            for (int j = 0; j < n; j++) {
                if (in[j][i] == 1) {
                    cnt++;
                    b = max(b, cnt);
                } else {
                    cnt = 0;
                }
            }
        }

        cp_in();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                cnt = 0;
                if (s[i][j] == 1) dfs(i, j, 1);
                c = max(c, cnt);
            }
        }

        cp_in();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                cnt = 0;
                if (s[i][j] == 1) dfs(i, j, 0);
                d = max(d, cnt);
            }
        }    
        cout << max(max(a, b), max(c, d)) << endl;
    }
    return 0;
}

