bool line(vector<string> v, int len)
{
	int n = v.size();


	rep(i, len)
	{
		int x = len - 1 + (n - 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = (n - 1) * (n - len + 1) + (n - 1) * i + 1;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = n - len + (n + 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	rep(i, len)
	{
		int x = n * (n - len) + (n + 1) * i;
		if(v[x / n][x % n] == '0') break;
		else if(i + 1 == len) return true;
	}

	return false;
}
int main()
{
    int n;
	while(cin >> n)
	{
		vector<string> v(n);
		rep(i, n) cin >> v[i];

		rep(i, n)
		{
			if(count(ALL(v[i]), '0') == 0)
			{
				cout << n << endl;
				goto ng;
			}

			rep(j, n)
			{
				if(v[j][i] == '0') break;
				else if(j + 1 == n)
				{
					cout << n << endl;
					goto ng;
				}
			}
		}

		for(int i = n; 0 < i; i--) if(line(v, i)) 
		{
			cout << i << endl;
			goto ng;
		}

		cout  << 0 << endl;

		ng:;
	}
}

