int diagLLUR(int n,int** mat){
	int res = 0;
	for(int y=0;y<n;y++){
		int isok = 1;
		for(int x=0,dy=y;x<n,dy>=0;x++,dy--){
			if(mat[dy][x] == 0) isok = 0;
		}
		res = max((y+1)*isok,res);
	}

	for(int x=0;x<n;x++){
		int isok = 1;
		for(int dx=x,y=n-1;dx<n,y>=0;dx++,y--){
			if(mat[y][dx] == 0) isok = 0;
		}
		res = max((n-x)*isok,res);
	}

	return res;
}

int diagULLR(int n,int** mat){
	int res = 0;
	for(int x=n-1;x>=0;x--){
		int isok = 1;
		for(int dx=x,y=0;dx<n,y<n;dx++,y++){
			if(mat[y][dx] == 0) isok = 0;
		}
		res = max((n-x)*isok,res);
	}

	for(int y=0;y<n;y++){
		int isok = 1;
		for(int x=0,dy=y;x<n,dy<n;x++,dy++){
			if(mat[dy][x] == 0) isok = 0;
		}
		res = max((n-y)*isok,res);
	}

	return res;
}

int vert(int n,int** mat){
	int res = 0;
	for(int x=0;x<n;x++){
		int isok = 1;
		for(int y=0;y<n;y++){
			if(mat[y][x] == 0) isok = 0;
		}

		res = max(n*isok,res);
	}

	return res;
}

int hori(int n,int** mat){
	int res = 0;
	for(int y=0;y<n;y++){
		int isok = 1;
		for(int x=0;x<n;x++){
			if(mat[y][x] == 0) isok = 0;
		}

		res = max(n*isok,res);
	}

	return res;
}

