int diagLLUR(int n){
	int res = 0;
	for(int y=0;y<n;y++){
		bool isok = true;
		for(int x=0,dy=y;x<n,dy>=0;x++,dy--){
			if(mat[dy][x] == 0) isok = false;
		}
		res = max((y+1)*(int)isok,res);
	}

	for(int x=0;x<n;x++){
		bool isok = true;
		for(int dx=x,y=n-1;dx<n,y>=0;dx++,y--){
			if(mat[y][dx] == 0) isok = false;
		}
		res = max((n-x)*(int)isok,res);
	}

	return res;
}

int diagULLR(int n){
	int res = 0;
	for(int x=n-1;x>=0;x--){
		bool isok = true;
		for(int dx=x,y=0;dx<n,y<n;dx++,y++){
			if(mat[y][dx] == 0) isok = false;
		}
		res = max((n-x)*(int)isok,res);
	}

	for(int y=0;y<n;y++){
		bool isok = true;
		for(int x=0,dy=y;x<n,dy<n;x++,dy++){
			if(mat[dy][x] == 0) isok = false;
		}
		res = max((n-y)*(int)isok,res);
	}

	return res;
}

