                char c; cin >> c;
                board[i][j] = c - '0';
            }
        }

        int ans = 0;
        for(int i=0; i<N; i++) {
            chmax(ans, solve(i, 0));
            chmax(ans, solve(0, i));
            chmax(ans, solve(N-1, i));
            chmax(ans, solve(i, N-1));
        }
        cout << ans << endl;
    }
    return 0;
}

