                    } else {
                        hor[i][j] = grid[i][j];
                    }
                    if(i - 1 >= 0) {
                        ver[i][j] = ver[i - 1][j] + 1;
                    } else {
                        ver[i][j] = grid[i][j];
                    }
                    if(i - 1 >= 0 && j - 1 >= 0) {
                        diagp[i][j] = diagp[i - 1][j - 1] + 1;
                    } else {
                        diagp[i][j] = grid[i][j];
                    }
                    if(i - 1 >= 0 && j + 1 < n) {
                        diago[i][j] = diago[i - 1][j + 1] + 1;
                    } else {
                        diago[i][j] = grid[i][j];
                    }
                }
                ans = max(ans, hor[i][j]);
                ans = max(ans, ver[i][j]);
                ans = max(ans, diagp[i][j]);
                ans = max(ans, diago[i][j]);
            }
        }
        printf("%d\n", ans);
    }
    return 0;
}

