int calc(int sy, int sx) 
{
    int res = 0;
    int ny = y, nx = x;
    ny += sy;
    nx += sx;
    while(ny >= 0 and ny < n and nx >= 0 and nx < n)
    {
        if(table[ny][nx] == '0') break;
        res++;
        ny += sy;
        nx += sx;
    }
    return res;
}
int search()
{
    int res = 0;
    res = max(res, calc(1, 0) + calc(-1, 0));
    res = max(res, calc(0, 1) + calc(0, -1));
    res = max(res, calc(-1, 1) + calc(1, -1));
    res = max(res, calc(-1, -1) + calc(1, 1));
    return res + 1;
}
