template<class T> inline bool chmax(T& a, T b) { if (a < b) { a = b; return 1; } return 0; }
template<class T> inline bool chmin(T& a, T b) { if (a > b) { a = b; return 1; } return 0; }
// ---------------------------------------------------------------------------

int N;
vector<string> G;

int solve(int row,int col){
    int res = 0;
    for(int k=0; k<6; k++){
        int x = row;
        int y = col;
        int now = 0;
        while(x>=0 and x<N and y<N and y>=0){
            if(G[x][y]-'0' > 0) now++;
            else{
                chmax(res,now);
                now = 0;
            }
            y += dy[k];
            x += dx[k];
        }
        chmax(res,now);
    }
    return res;
}

