					dp[1][y][x] = dp[1][y][x-1]+1;
					dp[2][y][x] = dp[2][y-1][x-1]+1;
					dp[3][y][x] = dp[3][y-1][x+1]+1;

					for(int i=0; i<4; i++){
						if(dp[i][y][x]>dp_max){
							dp_max = dp[i][y][x];
						}
					}
				}
			}
		}

		cout << dp_max << endl;
	}

	return 0;
}

