int check(int y,int x,char field[256][256]){
    int res = 0;
    int ny = y;
    int nx = x;
    int tmp=0;
    while(true){
        if(nx < 0 || nx >= n || ny < 0 || ny >= n || field[ny][nx] == '0')break;
        tmp++;
        ny++;
    }
    res = max(res,tmp);
    ny = y;nx= x;tmp = 0;
    while(true){
        if(nx < 0 || nx >= n || ny < 0 || ny >= n || field[ny][nx] == '0')break;
        tmp++;
        nx++;
    }
    res = max(res,tmp);
    ny = y;nx= x;tmp = 0;
    while(true){
        if(nx < 0 || nx >= n || ny < 0 || ny >= n || field[ny][nx] == '0')break;
        tmp++;
        nx++;
        ny++;
    }
    res = max(res,tmp);
    ny = y;nx= x;tmp = 0;
    while(true){
        if(nx < 0 || nx >= n || ny < 0 || ny >= n || field[ny][nx] == '0')break;
        tmp++;
        nx--;
        ny++;
    }
    res = max(res,tmp);
    ny = y;nx= x;tmp = 0;
    return res;
}
