int n,cnt ;
char table[260][260] ;
int dx[] = {-1,-1,0,1,1,1,0,-1} ;
int dy[] = {0,-1,-1,-1,0,1,1,1} ;

int f( int y , int x ){
	int cnt = 0 , c[4] = {} ;
	for( int k=0 ; k<8 ; k++ ){
		int nx = x+dx[k] , ny = y+dy[k] ;
		while( table[ny][nx] == '1' ){
			c[k%4]++ ;
			nx += dx[k] ;
			ny += dy[k] ;
		}
	}
	for( int k=0 ; k<4 ; k++ ) cnt = max( cnt , c[k]+1 ) ;
	return cnt ;
}

main(){
