			for( int j=0 ; j<=n+1 ; j++ ){
				if( !i || !j || i==n+1 || j==n+1 ) table[i][j] = '0' ;
				else cin >> table[i][j] ;
			}
		}

		int ans = 0 ;
		for( int i=1 ; i<=n ; i++ ){
			for( int j=1 ; j<=n ; j++ ){
				if( table[i][j] == '1' ){
					f(i,j) ;
					ans = max( ans , f(i,j) ) ;
				}
			}
		}

		cout << ans << endl ;
	}
}

