#include <stdio.h>

int main(){

  int number = 0;
  int i, j;
  int max = 0;
  int sum = 0;
  char string[1000] = {};


  scanf("%d", &number);
  while(number != 0){
  int matrix[number][number];

  for(i = 0; i < number; i++){
    scanf("%s", string);
    for(j = 0; j < number; j++){
      matrix[i][j] =  string[j] - 48;
    }
  }
  for(i = 0; i < number; i++){//横
    for(j = 0; j < number; j++){
      sum += matrix[i][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }
  for(i = 0; i < number; i++){//縦
    for(j = 0; j < number; j++){
      sum += matrix[j][i];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }


  for(i = 0; i < number; i++){//右肩上がり
    for(j = 0; j <= i; j++){
      sum += matrix[i - j][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }

  for(i = 0; i < number - 1; i++){
    for(j = 0; j <= i; j++){
      sum += matrix[(number - 1) - (i - j)][(number - 1) - j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }

  for(i = 0; i < number; i++){//右肩下がり
    for(j = 0; j <= i; j++){
      sum += matrix[(number - 1) - (i - j)][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }
  for(i = 0; i < number - 1; i++){
    for(j = 0; j <= i; j++){
      sum += matrix[i - j][(number - 1) - j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }


  printf("%d\n", max);
  max = 0;
  scanf("%d", &number);
  }
  return 0;
}

#include<stdio.h>
#include<string.h>
int main(){
	int i,j,n,ans;
	char s[260][260];
	while(1){
		scanf("%d",&n);
		if(n==0)break;
		ans=0;
		for(i=0;i<n;i++)scanf("%s",s[i]);
		for(i=0;i<n;i++){
			for(j=0;j<=i;j++)
				if(s[i-j][j]=='0')break;
			if(j==i+1 && j>ans)ans=i+1;
			for(j=0;j<=i;j++)
				if(s[j][n-1-(i-j)]=='0')break;
			if(j==i+1 && j>ans)ans=i+1;
			if(i<n){
				for(j=0;j<=i;j++)
					if(s[n-1-(i-j)][n-1-j]=='0')break;
				if(j==i+1 && j>ans)ans=i+1;
				for(j=0;j<=i;j++)
					if(s[n-1-(i-j)][j]=='0')break;
				if(j==i+1 && j>ans)ans=i+1;
			}
			for(j=0;j<n;j++)if(s[i][j]=='0')break;
			if(j==n && n>ans)ans=j;
			for(j=0;j<n;j++)if(s[j][i]=='0')break;
			if(j==n && n>ans)ans=j;
		}
		printf("%d\n",ans);
	}
	return 0;
}

#include <stdio.h>

int main() {
	char grid[255][256];
	int n;
	int i,j,k;
	int ones;
	int max_ones;

	while (1) {
		scanf("%d",&n);
		if (n == 0) {
			break;
		}
		for (i = 0;i != n;++i) {
			scanf("%s",grid[i]);
		}
		max_ones = 0;
		// vertical
		for (ones = 0,i = 0;i != n;++i) {
			for (j = 0;j != n;++j) {
				if (grid[i][j] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		// horizontal
		for (ones = 0,j = 0;j != n;++j) {
			for (i = 0;i != n;++i) {
				if (grid[i][j] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		// diagonal
		for (ones = 0,j = 0;j != n;++j) {
			for (i = 0,k = j;k != -1;++i,--k) {
				if (grid[i][k] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		for (ones = 0,i = 1;i != n;++i) {
			for (k = i,j = n-1;k != n;++k,--j) {
				if (grid[k][i] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		for (ones = 0,j = 0;j != n;++j) {
			for (i = 0,k = j;k != n;++i,++k) {
				if (grid[i][k] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		for (ones = 0,i = 1;i != n;++i) {
			for (k = i,j = 0;k != n;++k,++j) {
				if (grid[k][i] == '1') {
					++ones;
				} else {
					if (ones > max_ones) {
						max_ones = ones;
					}
					ones = 0;
				}
			}
			if (ones > max_ones) {
				max_ones = ones;
			}
			ones = 0;
		}
		printf("%d\n",max_ones);
	}
	return 0;
}

