#define MAX(x,y) ((x>y)?x:y)

char x[260][260];
int main(){
	int i,j,n,ans,a,b,c,d;

	while(1){
		scanf("%d",&n);
		if(n==0)break;
		memset(x,0,sizeof(x));
		for(i=0;i<n;i++)scanf("%s",x[i]);
		ans=0;
		for(i=0;i<n;i++){
			a=b=c=d=0;
			for(j=0;j<n;j++){
				if(x[i][j]=='1')a++;
				else{ans=MAX(ans,a);a=0;}

				if(x[j][i]=='1')b++;
				else{ans=MAX(ans,b);b=0;}

				if(j<=i){
					if(x[i-j][j]=='1')c++;
					else{ans=MAX(ans,c);c=0;}

					if(x[i-j][n-1-j]=='1')d++;
					else{ans=MAX(ans,d);d=0;}

					if(j==i){
						ans=MAX(ans,c);
						ans=MAX(ans,d);
						c=d=0;
					}
				}
				else{
					if(x[n+i-j][j]=='1')c++;
					else{ans=MAX(ans,c);c=0;}

					if(x[n+i-j][n-1-j]=='1')d++;
					else{ans=MAX(ans,d);d=0;}
				}
			}
			ans=MAX(ans,a);
