            break;
        }

        max_len = 0;

        for (y = 0; y < n; y++){
            scanf("%s", in);
            for (x = 0; x < n; x++){
                if (in[x] == '0'){
                    map[y][x] = 0;
                }
                else {
                    map[y][x] = 1;
                }
            }
        }

        // ¡
        for (y = 0; y < n; y++){
            get_maxlen(0);
            for (x = 0; x < n; x++){
                get_maxlen(map[y][x]);
            }
            get_maxlen(0);
        }

        // c
        for (x = 0; x < n; x++){
            get_maxlen(0);
            for (y = 0; y < n; y++){
                get_maxlen(map[y][x]);
            }
            get_maxlen(0);
        }


        // EÎß
        for (y = n - 1; y >= 0; y--){
            d = 0;
            x = 0;
            get_maxlen(0);
            do {
                get_maxlen(map[y + d][x + d]);
                d++;
            } while (x + d < n && y + d < n);
            get_maxlen(0);
        }
        for (x = 1; x < n; x++){
            d = 0;
            y = 0;
            get_maxlen(0);
            do {
                get_maxlen(map[y + d][x + d]);
                d++;
            } while (x + d < n && y + d < n);
            get_maxlen(0);
        }

        // ¶Îß
        for (x = 0; x < n; x++){
            d = 0;
            y = 0;
            get_maxlen(0);
            do {
                get_maxlen(map[y + d][x - d]);
                d++;
            } while (x - d >= 0 && y + d < n);
            get_maxlen(0);
        }
        for (y = 1; y < n; y++){
            d = 0;
            x = n - 1;
            get_maxlen(0);
            do {
                get_maxlen(map[y + d][x - d]);
                d++;
            } while (x - d < n && y + d < n);
            get_maxlen(0);
        }

        printf("%d\n", max_len);
    }
    return (0);
}

