#include <stdio.h>

#define N 256
#define MAX(A,B) \
  ( (A) > (B) ? (A) : (B) )

char  d[ N ][ N ];
int   n;

static
int scan (
  int x,
  int y,
  int sx,
  int sy,
  int res
  )
{
  int c;

  for ( c = 0; x >= 0 && y >= 0
            && x < n && y < n;
      x += sx, y += sy )
  {
    if ( d[ x ][ y ] == '0' )
    {
      res = MAX( res, c );
      c = 0;
      continue ;
    }

    ++c;
  }
  res = MAX( res, c );

  return ( res );
}

/** Application main entry point. */
int main (
  int     argc,
  char  * argv[ ]
  )
{
  int i;

  for ( ; ; )
  {
    int res = 0;

    scanf ( "%d", &n );
    if ( !( n ) ) break ;

    for ( i = 0; i < n; ++i )
    {
      scanf ( "%s", d[ i ] );
    }

    for ( i = 0; i < n; ++i )
    {
      res = scan ( 0, i, 1, 0, res );
      res = scan ( i, 0, 0, 1, res );
      res = scan ( 0, i, 1, 1, res );
      res = scan ( 0, i, 1, -1, res );
    }
    for ( i = 1; i < n; ++i )
    {
      res = scan ( i, 0, 1, 1, res );
      res = scan ( i, n - 1, 1, -1, res );
    }

    printf ( "%d\n", res );
  }

  return ( 0 );
}

