#include <stdio.h>          // printf(), scanf()
#include <stdbool.h>

#define MAX_M 255

int n;
char matrix[MAX_M][MAX_M + 2];

int
solve()
{
	int max = 0;
	for (int y = 0; y < n; ++y)
	{
		int count = 0;
		for (int x = 0; x < n; ++x)
		{
			if (matrix[y][x] != '1')
			{
				if (count > max)
					max = count;

				count = 0;
			}
			else
				count++;
		}

		if (count > max)
			max = count;
	}

	for (int x = 0; x < n; ++x)
	{
		int count = 0;
		for (int y = 0; y < n; ++y)
		{
			if (matrix[y][x] != '1')
			{
				if (count > max)
					max = count;

				count = 0;
			}
			else
				count++;
		}

		if (count > max)
			max = count;
	}

	for (int i = -(n - 1); i < n; ++i)
	{
		int count = 0;
		int x = i;
		for (int y = 0; y < n; ++x, ++y)
		{
			if (x >= n)
				break;

			if (0 <= x)
			{
				if (matrix[y][x] != '1')
				{
					if (count > max)
						max = count;

					count = 0;
				}
				else
					count++;
			}
		}

		if (count > max)
			max = count;

		count = 0;
		int y = i;
		for (int x = n - 1; x >= 0; --x, ++y)
		{
			if (y >= n)
				break;

			if (0 <= y)
			{
				if (matrix[y][x] != '1')
				{
					if (count > max)
						max = count;

					count = 0;
				}
				else
				count++;
			}
		}

		if (count > max)
			max = count;
	}

	return max;
}

int
main(int argc, char **argv)
{
	while (true)
	{
		scanf("%d\n", &n);
		if (n == 0)
			break;

		for (int i = 0; i < n; ++i)
			fgets(&matrix[i][0], sizeof(matrix[0]), stdin);

		printf("%d\n", solve());
	}

	return 0;
}

