    for(j = 0; j < number; j++){
      sum += matrix[i][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }
  for(i = 0; i < number; i++){//縦
    for(j = 0; j < number; j++){
      sum += matrix[j][i];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }


  for(i = 0; i < number; i++){//右肩上がり
    for(j = 0; j <= i; j++){
      sum += matrix[i - j][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }
  for(i = 0; i < number - 1; i++){
    for(j = 0; j <= i; j++){
      sum += matrix[(number - 1) - (i - j)][(number - 1) - j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }

  for(i = 0; i < number; i++){//右肩下がり
    for(j = 0; j <= i; j++){
      sum += matrix[(number - 1) - (i - j)][j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }
  for(i = 0; i < number - 1; i++){
    for(j = 0; j <= i; j++){
      sum += matrix[i - j][(number - 1) - j];
    }
    if(max < sum){
      max = sum;
    }
    sum = 0;
  }

  printf("%d\n", max);
  max = 0;
  scanf("%d", &number);
  }
  return 0;
}

