public class Main{

	Scanner sc=new Scanner(System.in);

	int INF=1<<28;
	double EPS=1e-9;

	int[][] a;
	int n;

	void run(){
		for(;;){
			n=sc.nextInt();
			if(n==0){
				break;
			}
			a=new int[n][n];
			for(int j=0; j<n; j++){
				String s=sc.next();
				for(int i=0; i<n; i++){
					a[j][i]=s.charAt(i)-'0';
				}
			}
			solve();
		}
	}

	void solve(){
		int ans=0;
		for(int j=0; j<n; j++){
			for(int i=0, k=0; i<n; i++)
				ans=Math.max(ans, k=(k+1)*a[j][i]);
			for(int i=0, k=0; i<n; i++)
				ans=Math.max(ans, k=(k+1)*a[i][j]);
			for(int i=0, k=0; j+i<n; i++)
				ans=Math.max(ans, k=(k+1)*a[j+i][i]);
			for(int i=0, k=0; j-i>=0; i++)
				ans=Math.max(ans, k=(k+1)*a[j-i][i]);
			for(int i=0, k=0; j+i<n; i++)
				ans=Math.max(ans, k=(k+1)*a[j+i][n-1-i]);
			for(int i=0, k=0; j-i>=0; i++)
				ans=Math.max(ans, k=(k+1)*a[j-i][n-1-i]);
		}
		println(""+ans);

