	char[][] map;
	int max;
	int n;
	int[] dx = {-1,1,0,0,-1,1,-1,1};
	int[] dy = {0,0,-1,1,-1,-1,1,1};
	public void run(){
		Scanner scan = new Scanner(System.in);
		while(scan.hasNext()){
			n = scan.nextInt();
			if(n == 0){
				break;
			}
			map = new char[n][];
