			}
			out.println(ans);
		}
		out.flush();
	}

	static boolean rCheck(int y, int x) {
		if(y < 0 || x < 0 || y >= map.length || x >= map[y].length) return false;
		return true;
	}

	static int check(int y, int x, int v) {

		int vy = VY[v];
		int vx = VX[v];
		char first = map[y][x];
		if(first != '1') return 0;
		int ret = 0;
		while(true) {
			if(!rCheck(y,x)) break;
			if(first != map[y][x])  break;
			ret++;
			y += vy;
			x += vx;
		}

		return ret;
	}


}

