import java.util.*;
public class Main {

	static boolean[][] field;
	static int n;

	public static void main(String[] args) {
		Scanner in=new Scanner(System.in);
		while(true) {
			n=in.nextInt();
			if(n==0)break;

			int res=0;
			field=new boolean[n][n];

			for(int i=0;i<n;i++) {
				String str=in.next();
				char[] m=str.toCharArray();
				for(int j=0;j<n;j++) {
					field[i][j]= (m[j]=='1')? true :false;
				}
			}

			for(int i=0;i<n;i++) {
				for(int j=0;j<n;j++) {
					if(field[i][j]) {
						res=Math.max(res, countSystem(i,j));
					}
				}
			}

			System.out.println(res);
		}
	}

	static int countSystem(int y,int x) {
		int res=0;

		int dy[]= {-1,-1,0,1,1,1,1,0,-1},dx[]= {0,1,1,1,0,-1,-1,-1};
		for(int i=0;i<8;i++) {
			res=Math.max(res, 1+countOne(y,x,dy[i],dx[i]));
		}

		return res;
	}

	static int countOne(int y,int x,int dy,int dx) {
		int py=y,px=x,res=0;

		while(true) {
			//System.out.println(res);
			if(py+dy>=0 && py+dy<n && px+dx>=0 && px+dx<n && field[py+dy][px+dx]) {
				py+=dy;px+=dx;
			}
			else break;
			res++;
		}

		return res;
	}

}

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);
		int n = Integer.parseInt(scanner.nextLine());

		while (n != 0) {
			int grid[][] = new int[n + 2][n + 2];
			int maxLen = 0;

			for(int i = 1; i <= n; i++) {
				String s = scanner.nextLine();
				for(int j = 1; j <= n; j++) {
					grid[j][i] = Integer.parseInt(s.substring(j - 1, j));
				}
			}

			for(int x = 1; x <= n; x++) {
				for(int y = 1; y <= n; y++) {
					if (n - x + 1 <= maxLen && n - y + 1 <= maxLen) {
						break;
					}

					int len = 0;
					for(int px = x; px <= n; px++) {
						if (grid[px][y] == 0) {
							break;
						}
						len++;
					}
					if (len > maxLen) {maxLen = len;}

					len = 0;
					for(int py = y; py <= n; py++) {
						if (grid[x][py] == 0) {
							break;
						}
						len++;
					}
					if (len > maxLen) {maxLen = len;}

					len = 0;
					for(int px = x; px >= 1; px--) {
						if (grid[px][y + (x - px)] == 0) {
							break;
						}
						len++;
					}
					if (len > maxLen) {maxLen = len;}

					len = 0;
					for(int px = x; px <= n; px++) {
						if (grid[px][y + (px - x)] == 0) {
							break;
						}
						len++;
					}
					if (len > maxLen) {maxLen = len;}
				}
			}
			System.out.println(maxLen);
			n = Integer.parseInt(scanner.nextLine());
		}
	}

}

import java.io.BufferedReader;
import java.io.InputStreamReader;


public class Main {

	int D[][];
	final int dir[][]={{1, 0}, {1, 1}, {0, 1}, {-1, 1}};
	int N;
	BufferedReader input;

	int ni() throws Exception {
		return Integer.parseInt(input.readLine());
	}

	String ns() throws Exception {
		return input.readLine();
	}

	int search(int i, int j, int d){
		if(i<0||i>=N||j<0||j>=N||D[i][j]==0) return 0;
		else return 1+search(i+dir[d][0], j+dir[d][1], d);
	}

	int solve(){
		int max=0;
		for(int i=0;i<N;++i){
			for(int j=0;j<N;++j){
				for(int d=0;d<4;++d){
					int k=search(i, j, d);
					max=max>k?max:k;
				}
			}
		}
		return max;
	}

	void setD() throws Exception{
		D=new int[N][N];
		for(int i=0;i<N;++i){
			String s=ns();
			for(int j=0;j<N;++j){
				char c=s.charAt(j);
				if(c=='1') D[i][j]=1;
				else if(c=='0') D[i][j]=0;
			}
		}
	}

	void io(){
		input=new BufferedReader(new InputStreamReader(System.in));
		while(true){
			try {
				N=ni();
				if(N==0)break;
				setD();
				System.out.println(solve());

			} catch (Exception e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		new Main().io();
	}

}

