				String s = sc.next();
				for (int j = 0; j < n; j++) {
					a[i][j] = new Dp(s.charAt(j) - '0');
					if (a[i][j].number == 1) {
						if (i == 0 && j == 0) {
							a[i][j].height = 1;
							a[i][j].width  = 1;
							a[i][j].slopeN = 1;
							a[i][j].slopeP = 1;
