						max = a[i][j].height;
					}
					if (max < a[i][j].width) {
						max = a[i][j].width;
					}
					if (max < a[i][j].slopeP) {
						max = a[i][j].slopeP;
					}
					if (max < a[i][j].slopeN) {
						max = a[i][j].slopeN;
					}
				}
			}
			System.out.println(max);
		}
	}
}

