							a[i][j].height = 1;
							a[i][j].width = 1;
							a[i][j].slopeP = 1;
							a[i][j].slopeN = 1;
							max = 1;
						}
						if (i == 0) {
							a[i][j].height = 1;
							a[i][j].slopeP = 1;
							a[i][j].slopeN = 1;
							if (max < a[i][j].height) {
								max = a[i][j].height;
							}
							if (max < a[i][j].slopeP) {
								max = a[i][j].slopeP;
							}
							if (max < a[i][j].slopeN) {
								max = a[i][j].slopeN;
							}
						} else if (j == 0) {
							a[i][j].width = 1;
							a[i][j].slopeN = 1;
							a[i][j].slopeP = a[i - 1][j + 1].slopeP + 1;
							if (max < a[i][j].width) {
								max = a[i][j].width;
							}
							if (max < a[i][j].slopeP) {
								max = a[i][j].slopeP;
							}
							if (max < a[i][j].slopeN) {
								max = a[i][j].slopeN;
							}
						} else {
							a[i][j].height = a[i - 1][j].height + 1;
							a[i][j].width = a[i][j - 1].width + 1;
							a[i][j].slopeN = a[i - 1][j - 1].slopeN + 1;
							if (j + 1 < n) {
								a[i][j].slopeP = a[i - 1][j + 1].slopeP + 1;
							}
							if (max < a[i][j].height) {
								max = a[i][j].height;
							}
							if (max < a[i][j].width) {
								max = a[i][j].width;
							}
							if (max < a[i][j].slopeP) {
								max = a[i][j].slopeP;
							}
							if (max < a[i][j].slopeN) {
								max = a[i][j].slopeN;
							}
						}
					}
				}
			}
			System.out.println(max);
		}
	}
