				if( map[i][j] == 1 ){
					wid[j+1] = wid[j] + 1;
					len[i+1][j] = len[i][j] + 1;
					max = Math.max(wid[j+1], max);
					max = Math.max(len[i+1][j], max);
				}
			}
		}
