			}			
		}
		for(int i = max; i < map.length; i++){
			int count = 0;
			for(int j = 0; j <= i; j++){
				if( map[map.length-1-j][i-j] == 1 ){
					count++;
					max = Math.max(max, count);
				} else {					
					count = 0;
				}
			}			
		}  
		return max;
	}
	public  void solve() throws IOException{
		int n;
		while( (n = nextInt() ) != 0 ){
			String[] s = new String[n];
			int[][] map = new int[n][n];
			for(int i = 0; i < n; i++){
				s[i] = nextToken();
				for(int j = 0; j < n; j++){
					map[i][j] = s[i].charAt(j)-'0';
				}
			}
			writer.println(getMax(map));			
		}
		writer.flush();
	}
	public static void main (String args[]) throws IOException{
		new Main().run();
	}

	BufferedReader reader;
	StringTokenizer tokenizer;
	PrintWriter writer;

	public void run() throws IOException{
		try{
			reader = new BufferedReader(new InputStreamReader(System.in));
			tokenizer = null;
			writer = new PrintWriter(System.out);
			solve();
			reader.close();
			writer.close();
		} catch (Exception e){
			e.printStackTrace();
			System.exit(1);
		}
	}
	public int nextInt() throws IOException{
		return Integer.parseInt(nextToken());
	}		
	public String nextToken() throws IOException{
		while( tokenizer == null || !tokenizer.hasMoreTokens() ){
			tokenizer = new StringTokenizer(reader.readLine());
		}
		return tokenizer.nextToken();
	}
}

import java.util.Scanner;

public class Main{
	static class Dp {
		int number;
		int height, width, slopeP, slopeN;

		Dp(int number) {
			this.number = number;
			this.height = 0;
			this.width = 0;
			this.slopeP = 0;
			this.slopeN = 0;
		}
	}

	public static void main(String srgs[]) {
		Scanner sc = new Scanner(System.in);
		int n;
		Dp a[][];
		int max;

		while (true) {
			n = sc.nextInt();
			if (n == 0) {
				break;
			}
			a = new Dp[n][n];
			max = 0;
			for (int i = 0; i < n; i++) {
				String s = sc.next();
				for (int j = 0; j < n; j++) {
					a[i][j] = new Dp(s.charAt(j) - '0');
					if (a[i][j].number == 1) {
						if (i == 0 && j == 0) {
							a[i][j].height = 1;
							a[i][j].width = 1;
							a[i][j].slopeP = 1;
							a[i][j].slopeN = 1;
							max = 1;
						} else if (i == 0) {
							a[i][j].height = 1;
							a[i][j].slopeP = 1;
							a[i][j].slopeN = 1;
							if (max == 0) {
								max = 1;
							}
						} else if (j == 0) {
							a[i][j].height = a[i - 1][j].height + 1;
							a[i][j].width = 1;
							a[i][j].slopeN = 1;
							a[i][j].slopeP = a[i - 1][j + 1].slopeP + 1;
							if (max < Math.max(a[i][j].height, a[i][j].slopeP)) {
								max = Math.max(a[i][j].height, a[i][j].slopeP);
							}
						} else {
							a[i][j].height = a[i - 1][j].height + 1;
							a[i][j].width = a[i][j - 1].width + 1;
							a[i][j].slopeN = a[i - 1][j - 1].slopeN + 1;
							if (j + 1 < n) {
								a[i][j].slopeP = a[i - 1][j + 1].slopeP + 1;
							}
							if (max < a[i][j].height) {
								max = a[i][j].height;
							}
							if (max < a[i][j].width) {
								max = a[i][j].width;
							}
							if (max < a[i][j].slopeP) {
								max = a[i][j].slopeP;
							}
							if (max < a[i][j].slopeN) {
								max = a[i][j].slopeN;
							}
						}
					}
				}
			}
			System.out.println(max);
		}
	}
}

