            for (int i = 0; i < n; i++)
            {
                var s = Console.ReadLine().Trim();
                 var hsub = 0;
                for (int j = 0; j < n; j++)
                {
                    if (s[j] == '1')
                    {
                        map[i, j] = true;
                        map2[i, j] = true;
                        hsub++;
                        vsub[j]++;
                        if (i == n - 1) hvmax = Math.Max(hvmax, vsub[j]);
                    }
                    else
                    {
                        hvmax = Math.Max(hvmax, hsub);
                        hsub = 0;
                        hvmax = Math.Max(hvmax, vsub[j]);
                        vsub[j] = 0;
                    }
                }
                hvmax = Math.Max(hvmax, hsub);
            }
            var nmax = 0;
            for (int i = 0; i < n; i++)
                for (int j = 0; j < n; j++)
                {
                    if(map[i,j])
                    {
                        var t = getNum(map, 1, -1, i, j, 1);
                        nmax = Math.Max(nmax, t);
                    }
                    if (map2[i,j])
                    {
                        var t2 = getNum(map2, 1, 1, i, j, 1);
                        nmax = Math.Max(nmax, t2);
                    }
                }
            Console.WriteLine(Math.Max(hvmax, nmax));
        }
    }
    public static int getNum (bool[,] map , int dx ,int dy ,int x, int y , int r)
    {
        var n = map.GetLength(0);
        map[x, y] = false;
        var nx = x + dx;  var ny = y + dy;
        if (nx >= 0 && nx < n && ny >= 0 && ny < n && map[nx, ny]) { return getNum(map, dx, dy, nx, ny, r + 1); }
        else return r;
    }
}

