namespace ConsoleApplication1
{
    class Grid
    {
        readonly int[] moveX = new int[] { -1, 0, +1, 0, -1, +1 };
        readonly int[] moveY = new int[] { 0, -1, 0, +1, +1, +1 };

        readonly int n;

        char[][] map;

        public Grid(int n)
        {
            this.n = n;
            map = new char[n][];
        }

        public void MakeMap()
        {
            for (int i = 0; i < n; i++)
            {
                string input = Console.ReadLine();
                map[i] = input.ToCharArray();
            }
        }

        public int SearchMax()
        {
            int ret = 0;

            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < n; j++)
                {
                    if (map[i][j] == '0') continue;

                    ret = Math.Max(ret, Search(j, i));
                }
            }
            return ret;
        }

        private int Search(int x, int y)
        {
            int ret = 0;

            for (int i = 0; i < moveX.Length; i++)
            {
                int tempX = x;
                int tempY = y;
                int count = 1;

                while (true)
                {
                    tempX += moveX[i];
                    tempY += moveY[i];

                    if (IsValid(tempX, tempY))
                    {
                        if (map[tempY][tempX] == '1') count++;
                        else break;
                    }
                    else break;
                }
                ret = Math.Max(ret, count);
            }
            return ret;
        }

        private bool IsValid(int x, int y)
        {
            if (x < 0) return false;
            if (x >= n) return false;
            if (y < 0) return false;
            if (y >= n) return false;
            return true;
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            StringBuilder sb = new StringBuilder();

            while (true)
            {
                int n = int.Parse(Console.ReadLine());

                if (n == 0) break;

                Grid g = new Grid(n);
                g.MakeMap();
                sb.AppendLine(g.SearchMax().ToString());
            }
            Console.Write(sb);
        }
    }
}

