                    }
            }
            var ret = new int[3];
            ret[0] = Math.Max(htotal.Max(), vtotal.Max());
            ret[1] = Math.Max(getSum(map, 1, 1, 0), getSum(map, 1, -1, 0));
            ret[2] = Math.Max(getSum(map, -1, 1, n - 1), getSum(map, -1, -1, n - 1));
            Console.WriteLine(ret.Max());
        }
    }
    public static int getSum(bool[,] map, int dx, int dy, int x)
    {
        var n = map.GetLength(0);
        var tmax = 0;

        for (int y = 0; y < n; y++)
        {
            var subt = 0;
            for (int i = 0; i < n; i++)
            {
                var nx = x + dx * i; var ny = y + dy * i;
                if (nx >= 0 && nx < n && ny >= 0 && ny < n && map[nx, ny]) subt++;
            }
            tmax = Math.Max(tmax, subt);
        }
        return tmax;
    }
}

