const int N = 10107;

int a[N], b[N];
int na, nb;
int l[N], r[N];

int main() {
	ios_base::sync_with_stdio(false);
	int n;
	string s, t;
	cin >> n >> s >> t;

	for (int i = 1; i < n; ++i) {
		if (s[i] != s[i - 1]) {
			a[na++] = i;
		}

		if (t[i] != t[i - 1]) {
			b[nb++] = i;
		}
	}

	int ans = 1e9;
	bool eq = s[0] == t[0];
	for (int i = -n - 1; i <= n + 1; ++i) {
		if ((i % 2 == 0) != eq) {
			continue;
		}

		int nl = 0, nr = 0;

		if (i > 0) {
			fill(l, l + i, 0);
			nl = i;
		} else {
			fill(r, r + (-i), 0);
			nr = -i;
		}

		copy(a, a + na, l + nl);
		copy(b, b + nb, r + nr);

		nl += na;
		nr += nb;

		while (nl < nr) {
			l[nl++] = n;
		}

		while (nr < nl) {
			r[nr++] = n;
		}
		int nw = 0;
		for (int j = 0; j < nl; ++j) {
			nw += abs(l[j] - r[j]);
		}

		ans = min(ans, nw);
	}

	printf("%d\n", ans);
}

