int get(int x)
{
	int sum = 0;
	int j = 1;
	for(int i = 1; i <= x; i ++)
	{
		if(j > nt)
			sum += n;
		else
			sum += Wt[j];
		j ++;
	}
	for(int i = 1; i <= ns; i ++)
	{
		if(j > nt)
			sum += n - Ws[i];
		else
		{
			sum += abs(Wt[j] - Ws[i]);
			j ++;
		}
	}
	for(; j <= nt; j ++)
		sum += abs(n - Wt[j]);
	return sum;
}
int go(int x)
{
	int sum = 0;
	int j = 1;
	for(int i = 1; i <= x; i ++)
	{
		if(i > ns)
			sum += n;
		else
			sum += Ws[i];
	}
	for(int i = x + 1; i <= ns; i ++)
	{
		if(j > nt)
			sum += n - Ws[i];
		else
		{
			sum += abs(Wt[j] - Ws[i]);
			j ++;
		}
