    int ans = 0;
    for (int i = 0; i < n; i++) {
      if (s[i] != t[i]) ans++;
    }
    cout << ans << endl;
    return 0;
  }
  if (s[0] == '0') {
    for (int i = n; i >= 0; i--) S.push_back(pi(0, i & 1));
  } else {
    for (int i = n; i >= 1; i--) S.push_back(pi(0, i & 1));
  }
  for (int i = 1; i < n; i++) {
    if (s[i] == s[i - 1]) continue;
    S.push_back(pi(i, s[i] - '0'));
  }
  if (s[n - 1] == '0') {
    for (int i = 1; i <= n; i++) S.push_back(pi(n, i & 1));
  } else {
    for (int i = 0; i <= n; i++) S.push_back(pi(n, i & 1));
  }
  for (int i = 1; i < n; i++) {
    if (t[i] == t[i - 1]) continue;
    T.push_back(pi(i, t[i] - '0'));
  }
  int ans = n * n;
  for (int i = 0; i + T.size() - 1 < S.size(); i++) {
    if (S[i].second != T[0].second) continue;
    int cur = 0;
    for (int j = 0; j < S.size(); j++) {
      if (j < i) cur += abs(S[j].first);
      if (j > i + T.size() - 1) cur += abs(S[j].first - n); 
    }
    for (int j = 0; j < T.size(); j++) {
      cur += abs(S[i + j].first - T[j].first);
    }
    ans = min(ans, cur);
  }
  cout << ans << endl;
  return 0;
}

