  int ans = 1 << 30;
  if (s[0] != t[0]) {
    a.insert(a.begin(), 0); ans = min(ans, calc(a, b)); a.erase(a.begin());
    b.insert(b.begin(), 0); ans = min(ans, calc(a, b)); b.erase(b.begin());
  } else ans = calc(a, b);

